use std::ops::Add;#[derive(Debug)]pub struct Vec2<T: Add> {    pub x: T,    pub y: T}impl<T> Add for Vec2<T> where T: Add<Output = T> {    type Output = Vec2<T>;    fn add(self, other: Vec2<T>) -> Vec2<T> {        Vec2 {            x: self.x + other.x,            y: self.y + other.y        }    }}